import { useState } from "react";

const App = () => {
  const [value, setValue] = useState("");
  const [error, setError] = useState("");
  const [chatHistory, setChatHistory] = useState([]);

  const SurpriseOption = [
    "Who is the president of the United States?",
    "What is the capital of France?",
    "When is Halloween?",
  ];

  const surprise = () => {
    const randomIndex = Math.floor(Math.random() * SurpriseOption.length);
    setValue(SurpriseOption[randomIndex]);
  };

  const getResponse = async () => {
    if (!value) {
      setError("Error! Please enter a question");
      return;
    }
    try {
      const options = {
        method: 'POST',
        body: JSON.stringify({
          history: chatHistory.map(chatItem => ({
            role: chatItem.role,
            parts: [{ text: chatItem.parts }]
          })),
          message: value
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      };
      const response = await fetch("http://localhost:8000/gemini", options);
      const data = await response.text();
      console.log(data);
      setChatHistory(oldChatHistory => [
        ...oldChatHistory,
        { role: "user", parts: value },
        { role: "model", parts: data }
      ]);
      setValue("");
    } catch (error) {
      console.error(error);
      setError("Error! Something went wrong");
    }
  };

  const clear = () => {
    setValue("");
    setChatHistory([]);
    setError("");
  };

  return (
    <div className="app">
      <p> What do you want to know?
        <button className="surprise" onClick={surprise} disabled={!chatHistory.length}>Surprise Me</button>
      </p>
      <div className="input-container">
        <input
          className="input"
          value={value}
          placeholder="When is Christmas?"
          onChange={(e) => setValue(e.target.value)}
        />
        {!error && <button onClick={getResponse}>Ask Me</button>}
        {error && <button onClick={clear}>Clear</button>}
      </div>
      {error && <p>{error}</p>}
      <div className="search-result">
        {chatHistory.map((chatItem, _index) => (
          <div key={_index}>
            <p className="answer">{chatItem.role}: {chatItem.parts}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
