import { useState, useEffect, useRef } from 'react';

const App = () => {
  const [value, setValue] = useState('');
  const [error, setError] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);
  const [showPopup, setShowPopup] = useState('initial'); // 'initial', 'info', 'demo', 'chat'
  const [popupStep, setPopupStep] = useState(0);

  useEffect(() => {
    // Check if the user has already seen the pop-ups
    const seenPopups = localStorage.getItem('seenPopups');
    if (seenPopups) {
      setShowPopup('chat');
    }
  }, []);

  const openPopup = (type) => {
    setShowPopup(type);
  };

  const closePopup = () => {
    setShowPopup('chat');
    localStorage.setItem('seenPopups', 'true'); // Mark pop-ups as seen
  };

  const goToNextPopup = () => {
    if (popupStep === 0) {
      setPopupStep(1);
    } else if (popupStep === 1) {
      setPopupStep(2);
    } else {
      closePopup();
    }
  };

  const SurpriseOption = [
    'Who is the president of the United States?',
    'What is the capital of France?',
    'When is Halloween?',
  ];

  const surprise = () => {
    const randomIndex = Math.floor(Math.random() * SurpriseOption.length);
    setValue(SurpriseOption[randomIndex]);
  };

  const getResponse = async () => {
    if (!value) {
      setError('Error! Please enter a question');
      return;
    }
    setLoading(true);
    setChatHistory((oldChatHistory) => [
      ...oldChatHistory,
      { role: 'user', parts: value },
    ]);
    setValue('');
    try {
      const options = {
        method: 'POST',
        body: JSON.stringify({
          history: chatHistory.map((chatItem) => ({
            role: chatItem.role,
            parts: [{ text: chatItem.parts }],
          })),
          message: value,
        }),
        headers: {
          'Content-Type': 'application/json',
        },
      };
      const response = await fetch('https://gem-server.vercel.app/gemini', options);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.text();
      setChatHistory((oldChatHistory) => [
        ...oldChatHistory,
        { role: 'model', parts: data },
      ]);
    } catch (error) {
      console.error(error);
      setError('Error! Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  const clear = () => {
    setValue('');
    setChatHistory([]);
    setError('');
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatHistory]);

  return (
    <div className="app">
      {showPopup === 'initial' && (
        <div className="popup-background">
          <div className="popup-content">
            <h2 className="popup-title">Welcome to Friend Chatlog</h2>
            <p className="popup-body">
              This site allows you to chat with an AI. Click "Next" to see how it works.
            </p>
            <button className="popup-next" onClick={goToNextPopup}>Next</button>
          </div>
        </div>
      )}

      {showPopup === 'info' && (
        <div className="popup-background">
          <div className="popup-content">
            <h2 className="popup-title">Info</h2>
            <p className="popup-body">
              Here you'll find information about the site and how to use it.
            </p>
            <button className="popup-next" onClick={goToNextPopup}>Next</button>
          </div>
        </div>
      )}

      {showPopup === 'demo' && (
        <div className="popup-background">
          <div className="popup-content">
            <h2 className="popup-title">Demo</h2>
            <p className="popup-body">
              Here is a short demo on how to use the chat application.
            </p>
            <button className="popup-next" onClick={closePopup}>Go!</button>
          </div>
        </div>
      )}

      {showPopup === 'chat' && (
        <>
          <div className="header">
            <h1>Friend Chatlog</h1>
            <button onClick={() => openPopup('info')}>Info</button>
            <button onClick={() => openPopup('demo')}>How to Use</button>
          </div>
          <div className="chat-container">
            <div className="chat-window">
              {chatHistory.length === 0 && (
                <div className="placeholder">
                  <p>Start a conversation by asking a question!</p>
                </div>
              )}
              <div className="search-result">
                {chatHistory.map((chatItem, _index) => (
                  <div
                    key={_index}
                    className={`message-container ${chatItem.role}`}
                  >
                    <div className={`chat-item ${chatItem.role}`}>
                      <p className={`answer ${chatItem.role}`}>
                        {chatItem.role === 'user' ? 'You' : 'Friend'}: {chatItem.parts}
                      </p>
                    </div>
                  </div>
                ))}
                {loading && (
                  <div className="message-container model">
                    <div className="chat-item model">
                      <p className="answer model">
                        Friend:
                        <span className="loading-dots">
                          <span>.</span>
                          <span>.</span>
                          <span>.</span>
                        </span>
                      </p>
                    </div>
                  </div>
                )}
              </div>
              <div ref={chatEndRef} /> {/* Anchor for scrolling */}
            </div>
            <div className="input-container">
              <input
                className="input"
                value={value}
                placeholder="Ask me anything..."
                onChange={(e) => setValue(e.target.value)}
              />
              {!error && (
                <button
                  className="ask-button"
                  onClick={getResponse}
                  disabled={loading}
                >
                  Ask
                </button>
              )}
              {error && (
                <button className="clear-button" onClick={clear}>
                  Clear
                </button>
              )}
              <button
                className="surprise"
                onClick={surprise}
                disabled={!chatHistory.length || loading}
              >
                Surprise Me
              </button>
            </div>
            {error && <p className="error">{error}</p>}
          </div>
        </>
      )}
    </div>
  );
};

export default App;
