import React, { useState } from 'react';
import './index.css';

const App = () => {
  const [value, setValue] = useState('');
  const [error, setError] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);

  // const SurpriseOption = [
  //   'Who is the president of the United States?',
  //   'What is the capital of France?',
  //   'When is Halloween?',
  // ];

  // const surprise = () => {
  //   const randomIndex = Math.floor(Math.random() * SurpriseOption.length);
  //   setValue(SurpriseOption[randomIndex]);
  // };

  const getResponse = async () => {
    if (!value) {
      setError('Error! Please enter a question');
      return;
    }
    setLoading(true);
    setChatHistory(oldChatHistory => [
      ...oldChatHistory,
      { role: 'user', parts: value }
    ]);
    setValue('');
    try {
      const options = {
        method: 'POST',
        body: JSON.stringify({
          history: chatHistory.map(chatItem => ({
            role: chatItem.role,
            parts: [{ text: chatItem.parts }]
          })),
          message: value
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      };
      const response = await fetch('https://gem-server.vercel.app/gemini', options);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.text();
      setChatHistory(oldChatHistory => [
        ...oldChatHistory,
        { role: 'model', parts: data }
      ]);
    } catch (error) {
      console.error(error);
      setError('Error! Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  const clear = () => {
    setValue('');
    setChatHistory([]);
    setError('');
  };

  return (
    <div className='app'>
      <div className='header'>
        <h1>Friend Chatlog</h1>
      </div>
      <div className='chat-container'>
        <div className='chat-window'>
          <div className='search-result'>
            {chatHistory.map((chatItem, _index) => (
              <div key={_index} className={`message-container ${chatItem.role}`}>
                <div className={`chat-item ${chatItem.role}`}>
                  <p className={`answer ${chatItem.role}`}>
                    {chatItem.role === 'user' ? 'You' : 'Friend'}: {chatItem.parts}
                  </p>
                </div>
              </div>
            ))}
            {loading && (
              <div className='message-container model'>
                <div className='chat-item model'>
                  <p className='answer model'>Friend: Fetching response...</p>
                </div>
              </div>
            )}
          </div>
        </div>
        <div className='input-container'>
          <input
            className='input'
            value={value}
            placeholder='Ask me anything...'
            onChange={(e) => setValue(e.target.value)}
          />
          {!error && <button className='ask-button' onClick={getResponse} disabled={loading}>Ask</button>}
          {error && <button className='clear-button' onClick={clear}>Clear</button>}
          {/* <button className='surprise' onClick={surprise} disabled={!chatHistory.length || loading}>
            Surprise Me
          </button> */}
        </div>
        {error && <p className='error'>{error}</p>}
      </div>
    </div>
  );
};

export default App;
