import { useState, useEffect, useRef } from 'react';

const App = () => {
  const [value, setValue] = useState('');
  const [error, setError] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);
  const [showPopup, setShowPopup] = useState('info'); // 'info', 'demo', 'how-to', or null

  const openPopup = () => {
    setShowPopup('info');
  };

  const nextPopup = () => {
    if (showPopup === 'info') {
      setShowPopup('demo');
    } else if (showPopup === 'demo') {
      setShowPopup('how-to');
    } else if (showPopup === 'how-to') {
      setShowPopup(null);
      localStorage.setItem('popupsShown', 'true');
    }
  };

  const closePopup = () => {
    setShowPopup(null);
    localStorage.setItem('popupsShown', 'true');
  };

  const getResponse = async () => {
    if (!value) {
      setError('Error! Please enter a question');
      return;
    }
    setLoading(true);
    setChatHistory((oldChatHistory) => [
      ...oldChatHistory,
      { role: 'user', parts: value },
    ]);
    setValue('');
    try {
      const options = {
        method: 'POST',
        body: JSON.stringify({
          history: chatHistory.map((chatItem) => ({
            role: chatItem.role,
            parts: [{ text: chatItem.parts }],
          })),
          message: value,
        }),
        headers: {
          'Content-Type': 'application/json',
        },
      };
      const response = await fetch('https://gem-server.vercel.app/gemini', options);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.text();
      setChatHistory((oldChatHistory) => [
        ...oldChatHistory,
        { role: 'model', parts: data },
      ]);
    } catch (error) {
      console.error(error);
      setError('Error! Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  const clear = () => {
    setValue('');
    setChatHistory([]);
    setError('');
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatHistory]);

  useEffect(() => {
    // Check localStorage to determine if the pop-ups have been shown before
    if (!localStorage.getItem('popupsShown')) {
      openPopup();
    }
  }, []);

  const handlePopupClose = () => {
    localStorage.setItem('popupsShown', 'true');
    closePopup();
  };

  return (
    <div className="app">
      <div className="header">
        <h1>Friend Chatlog</h1>
      </div>
      <div className="chat-container">
        {showPopup && (
          <div className="popup-overlay">
            {showPopup === 'info' && (
              <div className="popup">
                <h2> Meet Auggies the Therapist! </h2>
                <p>Hello! I'm Asheesh Yadav, and I’ve developed “Auggie the Therapist” using Google’s Gemini AI for my Major Project in the EDUC 240 class here at SFU. “Auggie” is crafted for students, aiming to create a supportive and inclusive environment for all.</p>
                <p>Trained with insights to understand and respond with empathy, “Auggie” is more than just a chatbot. It’s designed to be both a comforting presence and a friendly companion who listens and engages with you through all of life’s ups and downs. Whether you’re experiencing a high, a low, or anything in between, “Auggie” is here to offer support, understanding, and friendship.</p>
                <p> The current educational models often overlook inclusivity, leaving certain groups underserved. With “Auggie,” I hope to fill that gap, providing a safe space where you can freely share your thoughts and feelings, and talk about anything that’s on your mind. It’s here to offer comfort, a listening ear, and a friendly chat whenever you need it.
                </p>
                <button className="popup-next" onClick={nextPopup}>Next</button>
                <button className="popup-close" onClick={handlePopupClose}>Close</button>
              </div>
            )}
            {showPopup === 'demo' && (
              <div className="popup">
                <h2>Disclaimer</h2>
                <p>While “Auggie” is designed to provide support and a listening ear, it is not a substitute for professional therapy. The AI is still in development, and errors may occur. If you have serious or urgent concerns, please seek the assistance of a qualified therapist or mental health professional. Your well-being is important, and professional support is essential for addressing significant issues.</p>
                <p>Remeber You are Perfect the way you are </p>
                <button className="popup-next" onClick={nextPopup}>Next</button>
                <button className="popup-close" onClick={handlePopupClose}>Close</button>
              </div>
            )}
            {showPopup === 'how-to' && (
              <div className="popup">
                <h2>How to Interact</h2>
                <p>This pop-up gives a brief guide on how to interact with the chat.</p>
                <button className="popup-next" onClick={handlePopupClose}>Go!</button>
                <button className="popup-close" onClick={handlePopupClose}>Close</button>
              </div>
            )}
          </div>
        )}
        <div className="chat-window">
          {chatHistory.length === 0 && !loading && (
            <div className="placeholder">
              <p>Start a conversation by asking a question!</p>
            </div>
          )}
          <div className="search-result">
            {chatHistory.map((chatItem, _index) => (
              <div
                key={_index}
                className={`message-container ${chatItem.role}`}
              >
                <div className={`chat-item ${chatItem.role}`}>
                  <p className={`answer ${chatItem.role}`}>
                    {chatItem.role === 'user' ? 'You' : 'Friend'}: {chatItem.parts}
                  </p>
                </div>
              </div>
            ))}
            {loading && (
              <div className="message-container model">
                <div className="chat-item model">
                  <p className="answer model">Friend: Fetching response...</p>
                </div>
              </div>
            )}
          </div>
          <div ref={chatEndRef} /> {/* Anchor for scrolling */}
        </div>
        <div className="input-container">
          <input
            className="input"
            value={value}
            placeholder="Ask me anything..."
            onChange={(e) => setValue(e.target.value)}
          />
          {!error && (
            <button
              className="ask-button"
              onClick={getResponse}
              disabled={loading}
            >
              SEND
            </button>
          )}
          {error && (
            <button className="clear-button" onClick={clear}>
              Clear
            </button>
          )}
        </div>
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default App;
